package pacote1;

public class Calculos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int X = 200;
		int Y = 50;
		int soma = X + Y;
		
		System.out.println( X + "+" + Y + "="+ soma);
		
		System.out.println( X + "*" + Y + "="+(X * Y));
		
		System.out.println( X + "/" + Y + "="+(X / Y));
		
		System.out.println( X + "-" + Y + "="+(X - Y));
		
		
	}

}
